  var start = url.indexOf(param); 
  if (start == -1) return url; 
  var end = start + param.length; 
  var charBefore = url.charAt(start-1); 
  if (charBefore != '?' && charBefore != '&') return url; 
  var charAfter = (url.length >= end+1) ? url.charAt(end) : ''; 
